#include <iostream>#include <iomanip>#include <utility>#include <cmath>#include <random>#include <vector>#include <map>#include <set>#include <deque>#include <queue>#include <stack>#include <string>#include <algorithm>using namespace std;#define rep(i,n) for(int i = 0; i<n; ++i)#define REP(i,n) for(int i = 1; i<=n; ++i)#define all(x) begin(x),end(x)#define show(obj) {for(auto x:obj)cout<<x<<' ';cout<<endl;}#define line "----------"typedef long long ll;typedef pair<int,int> P;typedef pair<ll,ll> LP;const int inf = 1001001000;const ll INF = 1LL<<60;const int MOD = (int)1e9 + 7;int main(){	ll x,k,d;	cin >> x >> k >> d;	ll t = abs(x) / d;	if(x >= 0){		ll gl1 = x - d*t;		ll gl2 = x - d*(t +1);		if(abs(gl1) < abs(gl2)){			if(t > k){				cout << x - d*k << endl;;			}else{				if((k - t) % 2 == 0){					cout << gl1 << endl;				}else{					cout << gl2 << endl;				}			}		}else{			if(t + 1 > k){				cout << x - d*k << endl;			}else{				if((k - (t+1)) % 2 == 0){					cout << gl2 << endl;				}else{					cout << gl1 << endl;				}			}		}	}else{			ll gl1 = x + d*t;		ll gl2 = x + d*(t +1);		if(abs(gl1) < abs(gl2)){			if(t > k){				cout << x + d*k << endl;;			}else{				if((k - t) % 2 == 0){					cout << gl1 << endl;				}else{					cout << gl2 << endl;				}			}		}else{			if(t + 1 > k){				cout << x + d*k << endl;			}else{				if((k - (t+1)) % 2 == 0){					cout << gl2 << endl;				}else{					cout << gl1 << endl;				}			}		}	}	return 0;}